/**
 * The core logic. Process the spreadsheet data however you want. 
 * In this case, we create HTML to be presented back to the user. 
 * We'll use inline comments to provide a step-by-step description 
 * of what we're doing:
 */
function handleQueryResponse(response) {
  /**
   * Use the visualization GadgetHelper class to handle errors 
   */
  if (!gadgetHelper.validateResponse(response)) {
    print("not query respone");
    return;     // Default error handling was done, just leave. 
  }

  var data = response.getDataTable();
  if (data.getNumberOfRows > 0)  {  
    html.push('
');
    html.push('擴大療癒法教師/習修者名錄搜尋結果');
    html.push('');
    print(data.getNumberOfRows + " results");

    for (var row = 0; row < data.getNumberOfRows(); row++) {
        composeTeacherInfo(row, data);
    }
  }
  else {     
    html.push('
');
    html.push('抱歉，擴大療癒法教師/習修者名錄中沒有符合您搜尋條件的結果');
    html.push('');
    print("no result");
  }
  setHtml(); 
}
function composeTeacherInfo(row, data) {
  /* 
  A timestamp
  B name
  C country
  D city
  E phone
  F email
  G lineID
  H website
  I level
  J fileLink    //don't show
  K language
  */

    html.push('
');
    html.push(escapeHtml(data.getFormattedValue(row, 1))); 
    //html.push(escapeHtml(data[row][1])); 
    html.push('');

    html.push('');
    html.push('
國家: ');    
    html.push(escapeHtml(data.getFormattedValue(row, 2))); 
    //html.push(escapeHtml(data[row][2]));    
    html.push('');  

    html.push('
城市: ');   
    html.push(escapeHtml(data.getFormattedValue(row, 3)));  
    //html.push(escapeHtml(data[row][3])); 
    html.push('');   

    html.push('
電話: ');    
    html.push(escapeHtml(data.getFormattedValue(row, 4)));   
    //html.push(escapeHtml(data[row][4]));  
    html.push('');   

    html.push('
電子郵件信箱: ');
    html.push(escapeHtml(data.getFormattedValue(row, 5)));
    //html.push(escapeHtml(data[row][5])); 
    html.push('
');

    html.push('
LINE ID 或 WeChat ID: ');
    html.push(escapeHtml(data.getFormattedValue(row, 6)));
    //html.push(escapeHtml(data[row][6])); 
    html.push('');  

    html.push('
網站: ');
    html.push(escapeHtml(data.getFormattedValue(row, 7))); 
    //html.push(escapeHtml(data[row][7]));                
    html.push('
');  

    //var levels = escapeHtml(data[row][8]).split('\n');
    var levels = escapeHtml(data.getFormattedValue(row, 8));
    html.push('
<td style="vertical-align: top;width:40px"">');
    html.push('資格: ');
    for (var i = 0; i < levels.length; i++) {
        html.push('');
        html.push(escapeHtml(levels[i])); 
        html.push('
');                       
    }
    html.push('');  

    html.push('
語言: ');
    html.push(escapeHtml(data.getFormattedValue(row, 10))); 
    //html.push(escapeHtml(data[row][9]));   
    html.push('');   
}

